//noinspection TypeScriptCheckImport
import { Component } from 'angular2/core';
import {Router, ROUTER_DIRECTIVES} from 'angular2/router';
import {log} from "typings/dist/support/cli";
import {Cookie} from 'ng2-cookies/ng2-cookies';
import {print} from "angular2/src/facade/lang";



@Component({
    templateUrl : './app/templates/loginform.html',
    styleUrls : ['./app/css/loginform.css'],
    directives :[ROUTER_DIRECTIVES]
})



export class loginForm{
    data = {};
    constructor(private _router: Router){}
    formSubmit(){
        var uname = this.data.username;
        var pass = this.data.password;
       var key = btoa(btoa(uname) + '??' + btoa(pass));

        document.cookie = "sessionID = "+key+';';
        if(uname == "admin" && pass == "admin"){
        this._router.navigate(['AdminArea']);
        }else {
            this._router.navigate(['AdminArea']);
           // print 'Please give correct credentials';
            //this._router.navigate(['DashboardArea']);
        }
    }
}